//#region Navigáció

function Scroll(irany){
    if (irany == 'scroll_le'){
    document.getElementById(irany).onwheel = function(event) {
        if (event.deltaY > 0) {
            let oldal = document.getElementById(irany).className.split(" ")[1];
            Oldalak(oldal, "le")
            
        }
    }
    } else {
    document.getElementById(irany).onwheel = function(event) {
        if (event.deltaY < 0) {
            let oldal = document.getElementById(irany).className.split(" ")[1];
            Oldalak(oldal, "fel")
        }
    }
    }
}

function Oldalak(oldal, irany){
        

        switch (oldal) {
            case "kezdes":
                        Navigacio("fooldal")
                break;
            case "fooldal":
                    if (irany == "le")
                        Navigacio("classok")
                    else
                        Navigacio("kezdes")
                break;
            case "classok":
                if (irany == "le")
                    Navigacio("archivum")  
                else
                    Navigacio("fooldal")
                                        
                break;
            case "archivum":
                if (irany == "le")
                        Navigacio("kocka")
                    else
                        Navigacio("classok")
                 break;
            case "kockak":
                if (irany == "le")
                        Navigacio("rolunk")
                    else
                        Navigacio("archivum")
                 break;
            case "rolunk":
                        Navigacio("kocka")
                     break;
          }
        setTimeout(() => {
            if (irany == "le")
                    window.scrollTo({ top:0, behavior: "instant"})
                else
                    window.scrollTo({ top: document.getElementById('tartalom').scrollHeight, behavior: "instant"});
        }, 500);
          
}

kezdooldal=true;
